import { Card } from "antd"

export default function ProductItem( { product }) {
    return (
        <Card className="bg-gray product-item">
            <Link to={`/product/${product.id}`}>
                <img 
                    style={{width: '100%'}}
                    src={product.image}
                    alt={product.name} />
            </Link>
            <div className="product-item-info">
                <h6 className="product-item-category">
                    {product.category}
                </h6>
                <h2 className="product-item-name">
                    {product.name}
                </h2>
                <p className="product-item-description">
                  {product.description}
                </p>
                <div className="product-item-more">
                    <Link to={"/"} className="product-item-link">
                        See More ...
                    </Link>
                    <span
                        className="text-gray">
                        USD {product.price}.00
                    </span>
                </div>
            </div>
        </Card>
    );
  }
